    static ArrayList<Killers> getListData(){
        ArrayList<Killers> list = new ArrayList<>();
        for (int pos=0; pos<namaPembunuh.length; pos++){
            Killers bunuh = new Killers();
            bunuh.setName(namaPembunuh[pos]);
            bunuh.setDetail(detailPem[pos]);
            bunuh.setPhoto(fotoKiller[pos]);
            bunuh.setBio(bioPem[pos]);
            list.add(bunuh);
        }
        return list;
    }
